void quiz(ENetPeer* currentPeer)
{
	int a = rand() % 100;
	int b = rand() % 300;
	int c = rand() % 79;
	int d = rand() % 30;
	int h = a + b;
	int j = c + d;
	int all = h + j;
	int free = rand() % 100 + 100;
	string gems = std::to_string(free);
	string timer = std::to_string(all);
	ofstream myfile;
	myfile.open("math.txt");
	myfile << timer;
	myfile.close();
	ofstream RizkyOnes;
	RizkyOnes.open("gems.txt");
	RizkyOnes << gems;
	RizkyOnes.close();
	for (currentPeer = server->peers;
		currentPeer < &server->peers[server->peerCount];
		++currentPeer)
	{
		if (currentPeer->state != ENET_PEER_STATE_CONNECTED)
			continue;
		GamePacket p3 = packetEnd(appendString(appendString(createPacket(), "OnConsoleMessage"), "`9** GTRA Daily : `3'" + std::to_string(h) + " + " + std::to_string(j) + "'`9 = ? Prize = `2" + gems + " `9(gems) !. `o(/as <answer>)."));
		ENetPacket* packet3 = enet_packet_create(p3.data,
			p3.len,
			ENET_PACKET_FLAG_RELIABLE);
		enet_peer_send(currentPeer, 0, packet3);
		//enet_peer_reset(currentPeer);
		delete p3.data;
	}
}

void timer()
{
	bool exist = std::experimental::filesystem::exists("math.txt");
	if (!exist)
	{
		ofstream ok("math.txt");
		ok << "iji" << endl;
		ok.close();

		ofstream sh("gems.txt");
		sh << 0 << endl;
		sh.close();
	}
	int timer = 0;
	int save = 0;
	std::ifstream ifs("saveworld.txt");
	std::string content((std::istreambuf_iterator<char>(ifs)),
		(std::istreambuf_iterator<char>()));
	timer = stoi(content);
	if (timer != 0)
	{
		save = timer - 1;
		ofstream myfile;
		myfile.open("saveworld.txt");
		myfile << save;
		myfile.close();
	}
	else
	{
		string online = "";
		int total = 0;
		int balance = 0;
		ENetPeer* currentPeer;

		for (currentPeer = server->peers;
			currentPeer < &server->peers[server->peerCount];
			++currentPeer)
		{
			if (currentPeer->state != ENET_PEER_STATE_CONNECTED)
				continue;
			if (((PlayerInfo*)(currentPeer->data))->adminLevel >= 0) {
				online += ((PlayerInfo*)(currentPeer->data))->displayName + "`o, `w";
				total++;
			}
		}
		balance = total * 5 + 30;
		timer = balance;
		ofstream myfile;
		myfile.open("saveworld.txt");
		myfile << timer;
		myfile.close();
		autosave();
		quiz(currentPeer);
	}
}


